mapper.update:
    	UPDATE
		HD_User
		SET
		PASS_WORD = #{pass_word}
		WHERE
		USER_NAME = #{user_name}

mapper.select:
SELECT COUNT(*) /*게시글 갯수*/ FROM HD_Board WHERE 1=1
AND TITL_NAME LIKE '%' #{search} '%'
AND CATE_GORY = #{cate_gory}

SELECT IDXX_NUMB /*순번*/ , TITL_NAME /*제목*/ , USER_NAME /*이름*/ , CONT_ENTS /*내용*/ , STAT_FLAG /*상태*/ , SUCC_DATE /*완료일자*/ , CREA_USER /*생성자*/ , CREA_DATE /*생성일자*/ , UPDA_USER /*수정자*/ , UPDA_DATE /*수정일자*/ , ROW_NUMBER() OVER (ORDER BY CREA_DATE) AS NUMB_KEYX /*순번*/ , ( SELECT CODE_NAME /*카테고리 이름*/ FROM HD_Code WHERE CLAS_CODE = 'CATE_GORY' /*클래스 코드가 카테고리*/ AND COMM_CODE = CATE_GORY) /*글 카테고리 코드에 맞는 이름*/ AS CATE_NAME /*카테고리*/ FROM HD_Board WHERE 1=1
AND TITL_NAME LIKE '%' #{search} '%'
AND CATE_GORY = #{cate_gory}
ORDER BY CREA_DATE DESC LIMIT #{pageStart}, #{perPageNum}

mapper.update:
UPDATE HD_Board SET STAT_FLAG = #{stat_flag} /*상태 플래그*/ , UPDA_DATE = now() /*수정일자*/
,SUCC_DATE = now()
WHERE IDXX_NUMB = #{idxx_numb}
